const breakpointList = ['xs', 'sm', 'md', 'lg', 'xl'];

/**
 * Determines whether passed string is a parsable expression
 */
function isAnExpression(str) {
  return str.charAt(0) === '<' || str.charAt(0) === '>';
}

/**
 * Returns true if currently active breakpoint matches the expression
 */
function isAnyActive(acceptedBreakpoints) {
  let found = false;
  const currentBreakPpoint = checkSize();
  acceptedBreakpoints.forEach((acceptedBreakpoint) => {
    if (acceptedBreakpoint === currentBreakPpoint) {
      found = true;
    }
  });
  return found;
}

/**
 * Splits the expression in into <|> [=] alias
 */
function splitExpression(str) {
  // Used operator
  const operator = str.charAt(0);
  // Include breakpoint equal to alias?
  const orEqual = str.charAt(1) === '=';

  /**
   * Index at which breakpoint name starts.
   *
   * For:  >sm, index = 1
   * For: >=sm, index = 2
   */
  const index = 1 + (orEqual ? 1 : 0);

  /**
   * The remaining part of the expression, after the operator, will be treated as the
   * breakpoint name to compare with
   */
  const breakpointName = str.slice(index);

  return {
    operator,
    orEqual,
    breakpointName,
  };
}

/**
 * Determines whether current breakpoint matches the expression given
 */
function isMatchingExpression(str) {
  const expression = splitExpression(str);

  // Get index of sought breakpoint in the list
  const pos = breakpointList.indexOf(expression.breakpointName);

  // Breakpoint found
  if (pos !== -1) {
    let start = 0;
    let end = 0;

    /**
     * Parsing viewport.is('<=md') we interate from smallest breakpoint ('xs') and end
     * at 'md' breakpoint, indicated in the expression,
     * That makes: start = 0, end = 2 (index of 'md' breakpoint)
     *
     * Parsing viewport.is('<md') we start at index 'xs' breakpoint, and end at
     * 'sm' breakpoint, one before 'md'.
     * Which makes: start = 0, end = 1
     */
    if (expression.operator === '<') {
      start = 0;
      end = expression.orEqual ? pos + 1 : pos;
    }
    /**
     * Parsing viewport.is('>=sm') we interate from breakpoint 'sm' and end at the end
     * of breakpoint list.
     * That makes: start = 1, end = undefined
     *
     * Parsing viewport.is('>sm') we start at breakpoint 'md' and end at the end of
     * breakpoint list.
     * Which makes: start = 2, end = undefined
     */
    if (expression.operator === '>') {
      start = expression.orEqual ? pos : pos + 1;
      end = undefined;
    }

    const acceptedBreakpoints = breakpointList.slice(start, end);
    // console.log('acceptedBreakpoints: ', acceptedBreakpoints);

    return isAnyActive(acceptedBreakpoints);
  }
  return false;
}

export default function is(str) {
  if (isAnExpression(str)) {
    return isMatchingExpression(str);
  }
  return false;
}

export default function getJquery() {
  return new Promise((resolve, reject) => {
    let $$;
    if (window.$) {
      $$ = window.$;
    } else if (window.jQuery) {
      $$ = window.jQuery;
    } else {
      console.error('Please make sure you loaded jQuery and added it to the window object');
      reject(new Error('Please make sure you loaded jQuery and added it to the window object'));
    }
    resolve($$);
  });
}

export default function getScrollBarWidth() {
  const inner = document.createElement('p');
  inner.style.width = '100%';
  inner.style.height = '200px';

  const outer = document.createElement('div');
  outer.style.position = 'absolute';
  outer.style.top = '0px';
  outer.style.left = '0px';
  outer.style.visibility = 'hidden';
  outer.style.width = '200px';
  outer.style.height = '150px';
  outer.style.overflow = 'hidden';
  outer.appendChild(inner);

  document.body.appendChild(outer);
  const w1 = inner.offsetWidth;
  outer.style.overflow = 'scroll';
  let w2 = inner.offsetWidth;
  if (w1 === w2) w2 = outer.clientWidth;

  document.body.removeChild(outer);

  return w1 - w2;
}

export default function hasScrollBar() {
  // The Modern solution
  if (typeof window.innerWidth === 'number') {
    return window.innerWidth > document.documentElement.clientWidth;
  }

  // rootElem for quirksmode
  const rootElem = document.documentElement || document.body;

  // Check overflow style property on body for fauxscrollbars
  let overflowStyle;

  if (typeof rootElem.currentStyle !== 'undefined') overflowStyle = rootElem.currentStyle.overflow;

  overflowStyle = overflowStyle || window.getComputedStyle(rootElem, '').overflow;

  // Also need to check the Y axis overflow
  let overflowYStyle;

  if (typeof rootElem.currentStyle !== 'undefined') {
    overflowYStyle = rootElem.currentStyle.overflowY;
  }

  overflowYStyle = overflowYStyle || window.getComputedStyle(rootElem, '').overflowY;

  const contentOverflows = rootElem.scrollHeight > rootElem.clientHeight;
  const overflowShown =
    /^(visible|auto)$/.test(overflowStyle) || /^(visible|auto)$/.test(overflowYStyle);
  const alwaysShowScroll = overflowStyle === 'scroll' || overflowYStyle === 'scroll';

  return (contentOverflows && overflowShown) || alwaysShowScroll;
}

